__NUXT_JSONP__("/blog/binding-style-class", (function(a,b,c,d,e,f){return {data:[{document:{id:"YMzeWhIAACkATZHT",uid:"binding-style-class",url:a,type:"post_type",href:"https:\u002F\u002Fjsuu.cdn.prismic.io\u002Fapi\u002Fv2\u002Fdocuments\u002Fsearch?ref=YNQnuBIAACkAcitp&q=%5B%5B%3Ad+%3D+at%28document.id%2C+%22YMzeWhIAACkATZHT%22%29+%5D%5D",tags:[],first_publication_date:"2021-06-18T17:56:49+0000",last_publication_date:"2021-06-18T17:59:02+0000",slugs:["angular-styling-part-ii"],linked_documents:[],lang:"en-us",alternate_languages:[],data:{title:[{type:c,text:"Angular styling part II",spans:[]}],post_excerpt:[{type:b,text:"Angular styling II",spans:[]}],featured_img_link:{link_type:"Web",url:"https:\u002F\u002Fimages.pexels.com\u002Fphotos\u002F39284\u002Fmacbook-apple-imac-computer-39284.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"},categories:{link_type:"Document"},sticky_post:a,body:[{slice_type:d,slice_label:a,items:[{}],primary:{header_rich_text_field:[{type:c,text:"Binding style class",spans:[]}]}},{slice_type:e,slice_label:a,items:[{}],primary:{paragraph_rich_text_field:[{type:b,text:"You have been noticed that every Angularjs component has a ...component.css. This is where place all your SASS\u002FCSS styling classes and ids. We can also use the inline style in your component.ts file, if you prefer ( as an array).",spans:[{start:59,end:f,type:"em"},{start:62,end:f,type:"strong"}]},{type:b,text:"There are few methods\u002Fways we can apply styling elements to components. Using property of class we can bind the style.",spans:[]}]}},{slice_type:d,slice_label:a,items:[{}],primary:{header_rich_text_field:[{type:c,text:"Define class as property",spans:[]}]}},{slice_type:e,slice_label:a,items:[{}],primary:{paragraph_rich_text_field:[{type:b,text:"In class definition declare property with class.",spans:[]},{type:b,text:"  \u002F\u002F style class   public btnStyle = 'btn';",spans:[]}]}},{slice_type:d,slice_label:a,items:[{}],primary:{header_rich_text_field:[{type:c,text:"Define CSS",spans:[]}]}},{slice_type:"code_slice",slice_label:a,items:[{}],primary:{cod_text_field:[{type:"preformatted",text:"In your app _component.css_ file define a simple button class\n\n.btn{    \nbackground-color:rgba(51, 134, 243, 0.986);    \nmargin: 5px;    \npadding: 4px;    \ncolor: white;    \nborder: 1px;    \nborder-radius: 2px;\n}",spans:[]}]}},{slice_type:d,slice_label:a,items:[{}],primary:{header_rich_text_field:[{type:c,text:"Bind the class",spans:[]}]}},{slice_type:e,slice_label:a,items:[{}],primary:{paragraph_rich_text_field:[{type:b,text:"In the final step bind the property of class (btnStyle) in component.html (your basic component template).",spans:[]},{type:b,text:"\u003Cbutton [class]=\"btnStyle\"\u003EOK\u003C\u002Fbutton\u003E",spans:[]},{type:b,text:"Class binding has higher precedence in Angular, that means when we use the both methods only the binding will work. So both can't be used.",spans:[]},{type:b,text:"So what we do when need to apply a set of styles, that is for another post.",spans:[]}]}}]}}}],fetch:{},mutations:[]}}(null,"paragraph","heading1","heading_slice","paragraph_slice",75)));