__NUXT_JSONP__("/blog/sometimes-we-would-like-to-have-same-route-with-different", (function(a,b,c,d,e,f,g){return {data:[{document:{id:"YM2G9xIAACoAUHkq",uid:"sometimes-we-would-like-to-have-same-route-with-different",url:a,type:"post_type",href:"https:\u002F\u002Fjsuu.cdn.prismic.io\u002Fapi\u002Fv2\u002Fdocuments\u002Fsearch?ref=YPBMfxIAAC4A8Ecs&q=%5B%5B%3Ad+%3D+at%28document.id%2C+%22YM2G9xIAACoAUHkq%22%29+%5D%5D",tags:[],first_publication_date:d,last_publication_date:d,slugs:["redirect-vue-route"],linked_documents:[],lang:"en-us",alternate_languages:[],data:{title:[{type:"heading1",text:"Redirect vue route",spans:[]}],post_excerpt:[{type:b,text:"How to redirect a route to another",spans:[]}],featured_img_link:{link_type:"Web",url:"https:\u002F\u002Fimages.unsplash.com\u002Fphoto-1600493572882-f098876ce680?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=750&q=80"},categories:{link_type:"Document"},sticky_post:a,body:[{slice_type:e,slice_label:a,items:[{}],primary:{paragraph_rich_text_field:[{type:b,text:"Sometimes we would like to have same route with different name, such guide to FAQ, help, how-to etc. Vue allow us to use the redirect feature for this purpose.",spans:[{start:69,end:74,type:c},{start:78,end:95,type:c}]},{type:b,text:"Redirect can be used in our index route file in the following format",spans:[{start:0,end:8,type:c}]}]}},{slice_type:"code_slice",slice_label:a,items:[{}],primary:{cod_text_field:[{type:"preformatted",text:"import Vue from \"vue\";\n ......\nVue.use(VueRouter);\nconst routes = \\[\n  {\n    path: \"\u002F\",\n    name: \"Line\",\n    component: LineChart,\n     },   \n  {\n    \u002F\u002F The redirect can also be targeting a named route:\n    path: \"\u002Fhelp\",\n    redirect: { name: 'About' }\n  } \n\\];\nconst router = new VueRouter({\n  routes\n});\n\nexport default router;",spans:[]}]}},{slice_type:e,slice_label:a,items:[{}],primary:{paragraph_rich_text_field:[{type:b,text:"In the above router configuration we redirect \u002Fhelp router to the named route \u002FAbout. For multiple route we have to reply on alias.",spans:[{start:f,end:g,type:c},{start:f,end:g,type:"strong"}]}]}}]}}}],fetch:{},mutations:[]}}(null,"paragraph","em","2021-06-19T05:56:18+0000","paragraph_slice",125,130)));